import './App.css';
import React, { useEffect, useState } from 'react';

let gridData = [];

function App() {

  const [data, setData] = useState([]);


  async function makeMove() {
    const stringGrid = gridData.map(row => row.join(',')).join(';'); // Convert 2D array to a string
    const rowWidth = gridData[0].length;

    const apiUrl = `https://localhost:7095/api/makemove?minesHit=0&stringGrid=${stringGrid}&rowWidth=${rowWidth}&dir=u`;

    console.log(apiUrl);

    const gridresponse = await fetch(apiUrl);
    const result = await gridresponse.json();
    console.log(result);
  }


  useEffect(() => {
    async function getGrid()
    {
      gridData = []
      const gridresponse = await fetch('https://localhost:7095/api/hello?width=6&height=6');
      const json = await gridresponse.json();
      let data = "";
      for (const key in json) {
        if (json.hasOwnProperty(key)) {
          const rowArray = json[key];
          gridData.push(rowArray);
          const updatedRow = rowArray.map(cell => (cell === 'M' ? '-' : cell));
          console.log(`Row ${key}:`, updatedRow);
          data += "|" + updatedRow.join('|') + "|<br>"
        }
      }
      console.log(gridData);
      setData(data);
    }


    getGrid();

  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Mine Game</h1>
      </header>
      <div className="App-body">
        <p id="grid" dangerouslySetInnerHTML={{ __html: data || 'Loading...' }} >

        </p>
        <div onClick={makeMove}><p>Move</p></div>
      </div>
    </div>
  );
}

export default App;
